static struct sk_buff *create_skb_with_fragments(struct net_device *dev,
                                                 int data_len)
{
    struct sk_buff *skb = alloc_skb(data_len + sizeof(struct skb_shared_info), GFP_ATOMIC);
    if (!skb)
        return NULL;

    skb->dev = dev;
    skb_put(skb, data_len);

    // Create fragment list
    struct sk_buff_head frag_list;
    INIT_LIST_HEAD(&frag_list);

    struct sk_buff *frag_skb = alloc_skb(data_len / 2, GFP_ATOMIC);
    if (!frag_skb)
        goto err_out;

    memcpy(frag_skb->data, skb->data, data_len / 2);
    skb_shinfo(frag_skb)->nr_frags = 0; // Initialize fragment information

    // Add fragment to list
    __pskb_pull_tail(frag_skb, data_len / 2);
    list_add_tail(&frag_skb->list, &frag_list);

    // Reassemble packet
    struct sk_buff *reassembled_skb = skb_reass(dev, skb, &frag_list, 0);
    if (!reassembled_skb)
        goto err_out;

    return reassembled_skb;

err_out:
    kfree_skb(skb);
    while (!list_empty(&frag_list)) {
        struct sk_buff *temp = list_entry(frag_list.next, struct sk_buff, list);
        list_del_init(&temp->list);
        kfree_skb(temp);
    }
    return NULL;
}

