            return f"You are accessing {resource_name}"
        else:
            return "You don't have permission to access this resource."

rm = ResourceManager()

# Create users
user1 = User('John', 'admin')
user2 = User('Jane', 'user')

# Add resources
rm.add_resource('Resource A', user1)
rm.add_resource('Resource B', user1)

