#include <linux/kernel.h>
#include <linux/fs.h>
#include <linux/pipe.h>

void watch_queue_set_size(struct pipe *pipe, unsigned int new_size) {
    /* Original implementation */
    pipe->size = new_size;

    /* Modified implementation to fix the vulnerability */
    struct file *file = pipe->fops->owner.data;
    struct inode *inode = file->f_dentry->d_inode;
    struct pipe_inode_info *pii = pipe_inode(inode);
    unsigned int old_size = pipe->size;
    pipe->size = new_size;
    pii->nr_accounted += new_size - old_size;
}

int main() {
    int socket_fd = socket(AF_UNIX, SOCK_STREAM, 0);
    unlink("/tmp/sock");
    bind(socket_fd, (struct sockaddr *) &((struct sockaddr_un){ .sun_path = "/tmp/sock" }), sizeof(struct sockaddr_un));
    listen(socket_fd, 1);

    struct pipe pipe;
    pipe_create(&init_pipe, &pipe);
    struct file *file = pipe.fops->owner.data;
    file->f_flags |= O_LARGEFILE;
    file->f_pos = 0;
    watch_queue_set_size(&pipe, PIPE_BUF);

    accept(socket_fd, NULL, NULL);

    return 0;
}
