{
    char *safe_pptable;

    if (pptable == NULL || pptable_size > MAX_PPTABLE_SIZE) {
        printk(KERN_ERR "Invalid pptable data\n");
        return;
    }

    // Allocate memory safely
    safe_pptable = kmalloc(pptable_size + 1, GFP_KERNEL);
    if (!safe_pptable) {
        printk(KERN_ERR "Memory allocation failed\n");
        return;
    }

    // Copy data safely
    strncpy(safe_pptable, pptable, pptable_size);
    safe_pptable[pptable_size] = '\0'; // Null terminate

    // Use safe_pptable...

    kfree(safe_pptable); // Free allocated memory
}

module_init(init_module);
module_exit(cleanup_module);

MODULE_LICENSE("GPL");
```

